import {
  isProgramError,
  type Address,
  type SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM,
  type SolanaError,
} from 'gill';
import { COUNTER_PROGRAM_ADDRESS } from '../programs';

/** AlreadyVisited: User has already visited - each wallet can only record one visit */
export const COUNTER_ERROR__ALREADY_VISITED = 0x1770; // 6000
/** IncrementFailed: Failed to increment the global visit count - counter overflow */
export const COUNTER_ERROR__INCREMENT_FAILED = 0x1771; // 6001

export type CounterError =
  | typeof COUNTER_ERROR__ALREADY_VISITED
  | typeof COUNTER_ERROR__INCREMENT_FAILED;

let counterErrorMessages: Record<CounterError, string> | undefined;
if (process.env.NODE_ENV !== 'production') {
  counterErrorMessages = {
    [COUNTER_ERROR__ALREADY_VISITED]: `User has already visited - each wallet can only record one visit`,
    [COUNTER_ERROR__INCREMENT_FAILED]: `Failed to increment the global visit count - counter overflow`,
  };
}

export function getCounterErrorMessage(code: CounterError): string {
  if (process.env.NODE_ENV !== 'production') {
    return (counterErrorMessages as Record<CounterError, string>)[code];
  }

  return 'Error message not available in production bundles.';
}

export function isCounterError<TProgramErrorCode extends CounterError>(
  error: unknown,
  transactionMessage: {
    instructions: Record<number, { programAddress: Address }>;
  },
  code?: TProgramErrorCode
): error is SolanaError<typeof SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM> &
  Readonly<{ context: Readonly<{ code: TProgramErrorCode }> }> {
  return isProgramError<TProgramErrorCode>(
    error,
    transactionMessage,
    COUNTER_PROGRAM_ADDRESS,
    code
  );
}
